import React, { useState } from 'react';

import './styles/Recipe.scss';
import { Card, Button, ListGroup, ListGroupItem, Accordion, Col } from 'react-bootstrap';
// Fontawesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';


const Recipie = ({ title, calories, image, ingredients, cautions }) => {
  const cautionsList = cautions.map(caution => (
    <li className="cautions-list-item">{ caution }</li>
  ));

  const [caretRotation, setCaretRotation] = useState({
    rotate: false
  });

  const updateCaretPosition = e => {
    console.log("updateCaretPosition")
    if (caretRotation == false) {
      console.log("caretRotation is false");
      setCaretRotation({
        rotate: true
      });
    } else
      console.log("caretRotation is true");
    setCaretRotation({
      rotate: false
    });
  }

  return (
    <Col lg={5} sm={10}>
      <Accordion defaultActiveKey="0">
        <Card className="recipe-card">
          <div className="card-img-container container">
            <Card.Img variant="top" src={ image } alt={ title } />
          </div>
          <Card.Header>
            <h2>{ title }</h2>
          </Card.Header>
          <div className="basic-info-container">
            <p className="calories">calories: {calories}</p>
            <div className="cautions-list-container">
              <p className="cautions">Cautions:</p>
              <ListGroup style={{ display: (cautionsList.length > 0 ? 'inherit' : 'none') }} className="cautions-list">
                { cautionsList }
              </ListGroup>
            </div>
          </div>
          <div className="recipie-list-container">
            <Accordion.Toggle style={{ transform: rotation }} className="expand-button" as={Button} variant="link" eventKey="1" onClick={updateCaretPosition}>
              <span>View Ingredients</span>
              <FontAwesomeIcon icon={ faCaretDown } color="black" />
            </Accordion.Toggle>
            <Accordion.Collapse id="ingredients" eventKey="1">
              <Card.Body>
                <ListGroup className="list-group-flush">
                  { ingredients.map(ingredient => (<ListGroupItem>{ingredient.text}</ListGroupItem>)) }
                </ListGroup>
              </Card.Body>
            </Accordion.Collapse>
          </div>
        </Card>
      </Accordion>
    </Col> 
  );
}

export default Recipie;
