import React, { useState } from 'react';
import './Recipe.module.scss';
import { Card, Button, ListGroup, ListGroupItem, Accordion, Collapse } from 'react-bootstrap';
// Fontawesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';

const Recipie = ({ title, calories, image, ingredients }) => {



  return(
    // <Card className="recipe-card">
    //   <Card.Img variant="top" src={image} alt={title} />
    //   <Card.Body>
    //     <Card.Title>{title}</Card.Title>
    //     <Card.Text>
    //       <span>calories: {calories}</span>
    //     </Card.Text>
    //     <div className="button-container">
    //       <Button className="recipe-button"  style={{ marginBottom: '1rem' }}>View Recipe</Button>
    //     </div>
    //   </Card.Body>
    //     <ListGroup className="list-group-flush">
    //       {ingredients.map(ingredient => (
    //         <ListGroupItem>{ingredient.text}</ListGroupItem>
    //       ))}
    //     </ListGroup>
    // </Card>


    <Accordion className="recipe-card" defaultActiveKey="0">
      <Card>
        <Card.Img variant="top" src={image} alt={title} />
        <Card.Header>
          <h2>{title}</h2>
        </Card.Header>
        <div className="transaction-list-container">
          <Accordion.Toggle className="history" as={Button} variant="link" eventKey="1">
            <span>View Recipe</span>
            <FontAwesomeIcon icon={faCaretDown} color="white" />
          </Accordion.Toggle>
          <Accordion.Collapse eventKey="1">
            <Card.Body>
              <ListGroup className="list-group-flush">
                {ingredients.map(ingredient => (<ListGroupItem>{ingredient.text}</ListGroupItem>))}
              </ListGroup>
            </Card.Body>
          </Accordion.Collapse>
        </div>
      </Card>
    </Accordion>
  );
}

export default Recipie;
