import React, { useState } from 'react';
import './Recipe.module.scss';
import {Card, Button, ListGroup, ListGroupItem, Modal, Collapse} from 'react-bootstrap';

const Recipie = ({ title, calories, image, ingredients }) => {

  const [activeModal, setActiveModal] = useState("");

  const openModal = () => {
    setActiveModal(title);
    console.log("active modal: ", activeModal);
  }

  return(
    <Card className="recipe-card">
      <Card.Img variant="top" src={image} alt={title} />
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <Card.Text>
          <span>calories: {calories}</span>
        </Card.Text>
        <div className="button-container">
          <Button className="recipe-button" onClick={openModal} style={{ marginBottom: '1rem' }}>View Recipe</Button>
        </div>
      </Card.Body>
        <ListGroup className="list-group-flush">
          {ingredients.map(ingredient => (
            <ListGroupItem>{ingredient.text}</ListGroupItem>
          ))}
        </ListGroup>
    </Card>
  );
}

export default Recipie;
